def decimal_to_base(decimal_num, base):
    if decimal_num == 0:
        return &#39;0&#39;

    result = []
    while decimal_num &gt; 0:
        remainder = decimal_num % base
        if remainder &lt; 10:
            result.append(str(remainder))
        else:
            result.append(chr(ord(&#39;A&#39;) + remainder - 10)) # For bases &gt; 10 (e.g., hexadecimal)
        decimal_num //= base

    return &#39;&#39;.join(result[::-1])

# Example usage:
