#include &lt;unistd.h&gt;
#include &lt;stdio.h&gt;

int main() {
    int pipefd[2];
    pid_t pid;

    if (pipe(pipefd) == -1) {
        perror(&quot;pipe&quot;);
        return 1;
    }

    pid = fork();
    if (pid == -1) {
        perror(&quot;fork&quot;);
        return 1;
    }

    if (pid == 0) { // Child process
        close(pipefd[0]); // Close read end
        write(pipefd[1], &quot;Hello from child!&quot;, 18);
        close(pipefd[1]);
    } else { // Parent process
        close(pipefd[1]); // Close write end
        char buffer[100];
        read(pipefd[0], buffer, sizeof(buffer));
        printf(&quot;Parent received: %s\n&quot;, buffer);
        close(pipefd[0]);
    }
    return 0;
}

