def identify_file_type_by_magic(data_chunk):
    if data_chunk.startswith(b&#39;\xFF\xD8&#39;):
        return &#39;JPEG Image&#39;
    elif data_chunk.startswith(b&#39;%PDF&#39;):
        return &#39;PDF Document&#39;
    elif data_chunk.startswith(b&#39;&lt;HTML&gt;&#39;) or data_chunk.startswith(b&#39;&lt;html&gt;&#39;):
        return &#39;HTML Document&#39;
    return &#39;Unknown&#39;

