pthread_mutex_t my_mutex = PTHREAD_MUTEX_INITIALIZER;

void *thread_function(void *arg) {
    pthread_mutex_lock(&amp;my_mutex);
    // Critical Section
    pthread_mutex_unlock(&amp;my_mutex);
    return NULL;
}

