import pygeoip

try:
    # Ensure &#39;optGeoIP/Geo.dat&#39; exists and is readable
    gi = pygeoip.GeoIP(&#39;optGeoIP/Geo.dat&#39;)
    ip = &#39;93.170.52.30&#39;
    rec = gi.record_by_name(ip)
    if rec:
        print(f&quot;IP: {ip}, Latitude: {rec[&#39;latitude&#39;]}, Longitude: {rec[&#39;longitude&#39;]}&quot;)
    else:
        print(f&quot;Location not found for {ip}&quot;)
except Exception as e:
    print(f&quot;Error initializing GeoIP or resolving IP: {e}&quot;)

import mechanize
def testProxy(url, proxy):
    browser = mechanize.Browser()
    browser.set_proxies(proxy)
    page = browser.open(url)
    source_code = page.read()
    print source_code

