def rot13(text):
    result = []
    for char in text:
        if &#39;a&#39; &lt;= char &lt;= &#39;z&#39;:
            result.append(chr(((ord(char) - ord(&#39;a&#39;) + 13) % 26) + ord(&#39;a&#39;)))
        elif &#39;A&#39; &lt;= char &lt;= &#39;Z&#39;:
            result.append(chr(((ord(char) - ord(&#39;A&#39;) + 13) % 26) + ord(&#39;A&#39;)))
        else:
            result.append(char)
    return &#39;&#39;.join(result)

