def rot_cipher(text, shift):
    result = &#39;&#39;
    for char in text:
        if char.isalpha():
            start = ord(&#39;A&#39;) if char.isupper() else ord(&#39;a&#39;)
            shifted_char = chr((ord(char) - start + shift) % 26 + start)
            result += shifted_char
        else:
            result += char
    return result

