def caesar_cipher_encrypt(text, shift):
    result = &quot;&quot;
    for char in text:
        if char.isalpha():
            start = ord(&#39;a&#39;) if char.islower() else ord(&#39;A&#39;)
            result += chr((ord(char) - start + shift) % 26 + start)
        else:
