import hashlib

def calculate_file_hash(filepath, hash_algo=&#39;sha256&#39;):
    hasher = hashlib.new(hash_algo)
    with open(filepath, &#39;rb&#39;) as f:
        while chunk := f.read(4096):
            hasher.update(chunk)
    return hasher.hexdigest()

