# Example Python script to integrate with a threat intelligence feed
import requests

def get_threat_intel(indicator):
    api_key = &quot;YOUR_API_KEY&quot;
    url = f&quot;https://api.threatintelprovider.com/v1/indicator/{indicator}&quot;
    headers = {&quot;Authorization&quot;: f&quot;Bearer {api_key}&quot;}
    response = requests.get(url, headers=headers)
    if response.status_code == 200:
        return response.json()
    else:
        return None

# Example usage
malicious_ip = &quot;192.0.2.1&quot;
intel = get_threat_intel(malicious_ip)
if intel:
    print(f&quot;Threat intelligence for {malicious_ip}: {intel}&quot;)
else:
    print(f&quot;No threat intelligence found for {malicious_ip}&quot;)

