BACKUP_DIR=&quot;/mnt/backups/daily&quot;
SOURCE_DIR=&quot;/var/www/html /etc/nginx&quot;

mkdir -p $BACKUP_DIR
tar -czvf $BACKUP_DIR/web_config_backup_$DATE.tar.gz $SOURCE_DIR

# Log backup status
echo &quot;Backup completed for $SOURCE_DIR to $BACKUP_DIR/web_config_backup_$DATE.tar.gz on $DATE&quot; &gt;&gt; /var/log/backup.log

