SELECT
Path,
ValueName,
Value,
HEX_TO_ASC(Value) AS Value2
INTO RegistryMRU.csv
FROM \HKCU
WHERE Path LIKE &#39;%MRU%&#39;
OR Path LIKE &#39;%recent%&#39;
OR Path LIKE &#39;%Used%&#39;
OR Path LIKE &#39;%Usage%&#39;
OR Path LIKE &#39;%Time%&#39;
OR Path LIKE &#39;%Date%&#39;
OR Path LIKE &#39;%Last%&#39;
OR Path LIKE &#39;%Updated%&#39;
OR Path LIKE &#39;%History%&#39;
OR Path LIKE &#39;%Accessed%&#39;
OR Path LIKE &#39;%Last%&#39;
OR ValueName LIKE &#39;%MRU%&#39;
OR ValueName LIKE &#39;%recent%&#39;
OR ValueName LIKE &#39;%Used%&#39;
OR ValueName LIKE &#39;%Usage%&#39;
OR ValueName LIKE &#39;%Time%&#39;
OR ValueName LIKE &#39;%Date%&#39;
OR ValueName LIKE &#39;%Last%&#39;
OR ValueName LIKE &#39;%Updated%&#39;
OR ValueName LIKE &#39;%History%&#39;
OR ValueName LIKE &#39;%Accessed%&#39;
OR ValueName LIKE &#39;%Last%&#39;
ORDER BY Path, ValueName

from _winreg import *
def sid2user(sid):
    try:
        key = OpenKey(HKEY_LOCAL_MACHINE,
                      &quot;SOFTWARE\\Microsoft\\Windows NT\\CurrentVersion\\ProfileList&quot; + &#39;\\&#39; + sid)
        (value, type) = QueryValueEx(key, &#39;ProfileImagePath&#39;)
        user = value.split(&#39;\\&#39;)[-1]
        return user
    except:
        return sid
