#include &lt;complex&gt;
#include &lt;iostream&gt;

int main() {
    std::complex&lt;double&gt; z1(1.0, 2.0); // 1 + 2i
    std::complex&lt;double&gt; z2(3.0, 4.0); // 3 + 4i
    std::complex&lt;double&gt; sum = z1 + z2; // (1+3) + (2+4)i = 4 + 6i
    std::cout &lt;&lt; &quot;Sum: &quot; &lt;&lt; sum &lt;&lt; std::endl;
    std::cout &lt;&lt; &quot;Real part of sum: &quot; &lt;&lt; sum.real() &lt;&lt; std::endl;
    std::cout &lt;&lt; &quot;Imaginary part of sum: &quot; &lt;&lt; sum.imag() &lt;&lt; std::endl;
    return 0;
}
