#include &lt;complex&gt;
#include &lt;iostream&gt;

int main() {
    std::complex&lt;double&gt; z1(1.0, 2.0); // Standard
    std::complex&lt;float&gt; z2(3.0f, 4.0f); // Standard
    std::complex&lt;long double&gt; z3(5.0L, 6.0L); // Standard
    // std::complex&lt;int&gt; z4(7, 8); // Not guaranteed to be portable or standard
    std::cout &lt;&lt; z1 &lt;&lt; std::endl;
    return 0;
}
