# Example PowerShell for checking device compliance (simplified)
$osVersion = (Get-ComputerInfo).OsVersion
$antivirusStatus = Get-MpComputerStatus | Select-Object AntivirusStatus

if ($osVersion -ge &#39;10.0.19044&#39; -and $antivirusStatus.AntivirusStatus -eq &#39;Valid&#39;) {
    Write-Host &quot;Device is compliant.&quot;
} else {
    Write-Host &quot;Device is NOT compliant.&quot;
}

def validate_score(score):
    try:
        score_int = int(score)
        if 0 &lt;= score_int &lt;= 5:
            return True
        else:
            return False # Score out of bounds
    except ValueError:
        return False # Not an integer

# Example usage:
print(f&quot;Validating &#39;3&#39;: {validate_score(&#39;3&#39;)}&quot;)
print(f&quot;Validating &#39;6&#39;: {validate_score(&#39;6&#39;)}&quot;)
print(f&quot;Validating &#39;abc&#39;: {validate_score(&#39;abc&#39;)}&quot;)

