resource &quot;aws_s3_bucket&quot; &quot;my_secure_bucket&quot; {
  bucket = &quot;my-unique-secure-bucket-name&quot;
}

resource &quot;aws_s3_bucket_public_access_block&quot; &quot;block_public_access&quot; {
  bucket = aws_s3_bucket.my_secure_bucket.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

resource &quot;aws_s3_bucket_public_access_block&quot; &quot;secure_bucket_block&quot; {
  bucket = aws_s3_bucket.my_secure_bucket.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

resource &quot;aws_s3_bucket&quot; &quot;my_secure_bucket&quot; {
  bucket = &quot;my-secure-application-data&quot;
  acl    = &quot;private&quot;

  # Ensure encryption at rest
  server_side_encryption_configuration {
    rule {
      apply_server_side_encryption_by_default {
        sse_algorithm = &quot;AES256&quot;
      }
    }
  }
}

resource &quot;aws_db_instance&quot; &quot;mydb&quot; {
  # ... other configurations ...
  password = &quot;MySuperSecretPassword123!&quot; # VIOLATION: Hardcoded secret
}

resource &quot;aws_lambda_function&quot; &quot;my_function&quot; {
  # ... other configurations ...
  environment {
    variables = {
      API_KEY = &quot;sk_live_abcdef1234567890&quot; # VIOLATION: Hardcoded API key
    }
  }
}

resource &quot;aws_security_group&quot; &quot;secure_default&quot; {
  name        = &quot;secure-default-sg&quot;
  description = &quot;Security group with no ingress rules&quot;
  vpc_id      = aws_vpc.main.id

  # No ingress rules defined, implicitly denies all inbound traffic

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = &quot;-1&quot;
    cidr_blocks = [&quot;0.0.0.0/0&quot;]
  }
}

module &quot;s3_bucket&quot; {
  source  = &quot;./modules/secure_s3_bucket&quot;
  bucket_name = &quot;my-secure-app-data&quot;
  environment = &quot;production&quot;
}

resource &quot;aws_s3_bucket&quot; &quot;bad_bucket&quot; {
  bucket = &quot;my-public-bucket&quot;
  acl    = &quot;public-read&quot;
}

resource &quot;aws_security_group&quot; &quot;no_public_ssh&quot; {
  name        = &quot;no-public-ssh-sg&quot;
  description = &quot;Security group that does not allow public SSH&quot;
  vpc_id      = aws_vpc.main.id

  ingress {
    description = &quot;Allow SSH from specific IP&quot;
    from_port   = 22
    to_port     = 22
    protocol    = &quot;tcp&quot;
    cidr_blocks = [&quot;203.0.113.0/24&quot;] # Example: only allow from a specific IP range
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = &quot;-1&quot;
    cidr_blocks = [&quot;0.0.0.0/0&quot;]
  }
}

resource &quot;aws_iam_user&quot; &quot;db_app_user&quot; {
  name = &quot;db_app_user&quot;
}

resource &quot;aws_iam_policy&quot; &quot;db_read_only&quot; {
  name        = &quot;db_read_only&quot;
  description = &quot;IAM policy for read-only access to specific database tables&quot;
  policy      = jsonencode({
    Version = &quot;2012-10-17&quot;
    Statement = [
      {
        Action = [
          &quot;rds-db:connect&quot;,
          &quot;rds-db:select&quot;
        ]
        Effect   = &quot;Allow&quot;
        Resource = [
          &quot;arn:aws:rds-db:us-east-1:123456789012:dbuser:mydb/db_app_user&quot;,
          &quot;arn:aws:rds-db:us-east-1:123456789012:db:mydb/table/orders&quot;
        ]
      }
    ]
  })
}

resource &quot;aws_iam_user_policy_attachment&quot; &quot;db_app_user_policy_attach&quot; {
  user       = aws_iam_user.db_app_user.name
  policy_arn = aws_iam_policy.db_read_only.arn
}

