&lt;PolicySet
    xmlns=&quot;urn:oasis:names:tc:xacml:3.0:core:schema:wd-17&quot;
    PolicySetId=&quot;OfflineDevicePolicySet&quot;
    Version=&quot;1.0&quot;
    CombiningAlgorithm=&quot;urn:oasis:names:tc:xacml:1.0:policy-combining-algorithm:deny-overrides&quot;&gt;
    &lt;Target&gt;
        &lt;AnyOf&gt;
            &lt;AllOf&gt;
                &lt;Match MatchId=&quot;urn:oasis:names:tc:xacml:1.0:function:string-equal&quot;&gt;
                    &lt;AttributeValue DataType=&quot;http://www.w3.org/2001/XMLSchema#string&quot;&gt;device-id-123&lt;/AttributeValue&gt;
                    &lt;AttributeDesignator Category=&quot;urn:oasis:names:tc:xacml:1.0:subject-category:access-subject&quot; AttributeId=&quot;urn:oasis:names:tc:xacml:1.0:subject:subject-id&quot; DataType=&quot;http://www.w3.org/2001/XMLSchema#string&quot; MustBePresent=&quot;true&quot;/&gt;
                &lt;/Match&gt;
            &lt;/AllOf&gt;
        &lt;/AnyOf&gt;
    &lt;/Target&gt;
    &lt;Policy PolicyId=&quot;AllowValetParking&quot; Version=&quot;1.0&quot; RuleCombiningAlgorithm=&quot;urn:oasis:names:tc:xacml:1.0:rule-combining-algorithm:permit-overrides&quot;&gt;
        &lt;Target&gt;
            &lt;AnyOf&gt;
                &lt;AllOf&gt;
                    &lt;Match MatchId=&quot;urn:oasis:names:tc:xacml:1.0:function:string-equal&quot;&gt;
                        &lt;AttributeValue DataType=&quot;http://www.w3.org/2001/XMLSchema#string&quot;&gt;valet&lt;/AttributeValue&gt;
                        &lt;AttributeDesignator Category=&quot;urn:oasis:names:tc:xacml:1.0:subject-category:access-subject&quot; AttributeId=&quot;urn:oasis:names:tc:xacml:1.0:subject:subject-role&quot; DataType=&quot;http://www.w3.org/2001/XMLSchema#string&quot; MustBePresent=&quot;true&quot;/&gt;
                    &lt;/Match&gt;
                &lt;/AllOf&gt;
            &lt;/AnyOf&gt;
        &lt;/Target&gt;
