--- # Example Hardening Template Snippet
  - name: Ensure SSH is configured securely
    lineinfile:
      path: /etc/ssh/sshd_config
      regexp: &#39;^PermitRootLogin&#39;
      line: &#39;PermitRootLogin no&#39;
      state: present
    notify: restart sshd
  - name: Disable unnecessary services
    service:
      name: &#39;{{ item }}&#39;
      state: stopped
      enabled: no
    loop:
      - &#39;telnet&#39;
      - &#39;ftp&#39;
      - &#39;rsh&#39;
  - name: Apply firewall rules
    ufw:
      rule: allow
      port: &#39;22&#39;
      proto: tcp
      from: &#39;{{ trusted_networks }}&#39;

