int
socket_common(int domain, int type, int protocol, struct proc *p, int delegate_pid)
{
    // ... initial setup ...

    /* Audit the socket creation (macOS only) */
    if (mac_socket_check_create(p-&gt;p_ucred, &amp;mac_policy_handle) != 0) {
        // Handle MACF policy violation
        return EPERM;
    }

    /* Delegate call requires specific privilege */
    if (delegate_pid != 0 &amp;&amp; !priv_check_cred(p-&gt;p_ucred, PRIV_NET_PRIVILEGED_SOCKET_DELEGATE, 0)) {
        // Handle privilege violation
        return EACCES;
    }

    // ... proceed with socket creation if security checks pass ...
    return screate_internal(domain, type, protocol, p, delegate_pid, &amp;so);
}

