public class Selection {
    public static void sort(Comparable[] a) {
        int N = a.length;
        for (int i = 0; i &lt; N; i++) {
            int min = i;
            for (int j = i + 1; j &lt; N; j++) {
                if (less(a[j], a[min])) min = j;
            }
            exch(a, i, min);
        }
    }
    // less() and exch() methods would be defined elsewhere
}

