import hashlib

def calculate_sha256(filepath):
    hasher = hashlib.sha256()
    with open(filepath, &#39;rb&#39;) as f:
        while True:
            chunk = f.read(4096)
            if not chunk:
                break
            hasher.update(chunk)
    return hasher.hexdigest()

