from cryptography.hazmat.primitives.ciphers import Cipher, algorithms, modes
from cryptography.hazmat.backends import default_backend

key = b&#39;a_very_secret_key_16&#39;
iv = b&#39;an_initialization_&#39;

algorithm = algorithms.AES(key)
cipher = Cipher(algorithm, modes.CBC(iv), backend=default_backend())
encryptor = cipher.encryptor()

plaintext = b&#39;This is a secret message that needs to be encrypted.&#39;
# AES operates on 128-bit (16-byte) blocks, so plaintext must be padded
padded_plaintext = plaintext + (16 - len(plaintext) % 16) * b&#39;\0&#39;

ciphertext = encryptor.update(padded_plaintext) + encryptor.finalize()

print(f&#39;AES Block Size: {algorithms.AES.block_size} bits&#39;)
print(f&#39;Ciphertext: {ciphertext.hex()}&#39;)

