uint32_t crc32(uint32_t crc, const void *buf, size_t size) {
    const uint8_t *p = buf;
    while (size--) {
        crc ^= *p++;
        for (int i = 0; i &lt; 8; i++) {
            crc = (crc &gt;&gt; 1) ^ (0xEDB88320 &amp; -(crc &amp; 1));
        }
    }
    return ~crc;
}

