    printf(&quot;Content written to mapped memory: %s\n&quot;, addr);

    munmap(addr, 4096);
    close(fd);
    return 0;
}

#include &lt;sys/types.h&gt;
#include &lt;stdio.h&gt;
#include &lt;unistd.h&gt;
#include &lt;sys/wait.h&gt;

int main()
{
    pid_t pid;
    pid = fork();

    if (pid &lt; 0) {
        fprintf(stderr, &quot;Fork Failed&quot;);
        return 1;
    }
    else if (pid == 0) { /* Child process */
        printf(&quot;Child process exiting.\n&quot;);
        // Child exits without parent waiting immediately
        return 0;
    }
    else { /* Parent process */
        // Parent does NOT call wait() immediately, creating a zombie
        printf(&quot;Parent process created child with PID %d. Parent not waiting immediately.\n&quot;, pid);
        sleep(10); // Parent sleeps, child becomes zombie
        int status;
        waitpid(pid, &amp;status, 0); // Parent eventually waits, cleaning up zombie
        printf(&quot;Parent collected child status. Child PID %d cleaned up.\n&quot;, pid);
    }
    return 0;
}

