#include &lt;unistd.h&gt;
#include &lt;stdio.h&gt;

int main() {
    pid_t pid = fork();
    if (pid == -1) {
        perror(&quot;fork&quot;);
        return 1;
    }
    if (pid == 0) {
        // Child process
        printf(&quot;Child process: PID = %d\n&quot;, getpid());
    } else {
        // Parent process
        printf(&quot;Parent process: PID = %d, Child PID = %d\n&quot;, getpid(), pid);
    }
    return 0;
}

