policy_document:
  title: &quot;Information Security Policy v1.0&quot;
  owner: &quot;CISO&quot;
  approval_date: &quot;2023-10-26&quot;
  review_cycle: &quot;annual&quot;
  scope: &quot;All organizational assets, personnel, and information systems&quot;
  sections:
    - title: &quot;Purpose&quot;
      content: &quot;To protect the confidentiality, integrity, and availability of information...&quot;
    - title: &quot;Scope&quot;
      content: &quot;This policy applies to all employees, contractors, and third parties...&quot;
    - title: &quot;Compliance&quot;
      content: &quot;Adherence to this policy is mandatory...&quot;

# Example of an ISMS scope statement, a key requirement of ISO 27001:2022 Clause 4.3
scope:
  organization_units:
    - &#39;IT Department&#39;
    - &#39;Product Development&#39;
  locations:
    - &#39;Headquarters, New York&#39;
    - &#39;Data Center, Virginia&#39;
  assets:
    - &#39;Customer Data (CRM)&#39;
    - &#39;Source Code (GitLab)&#39;
    - &#39;Production Servers (AWS)&#39;
  exclusions:
    - &#39;Physical Security of remote offices (managed by third-party)&#39;
  justification: &#39;Focus on core information assets and critical business processes.&#39;

