public class MyUtilityLibrary {
    public static int add(int a, int b) {
        return a + b;
    }

    public static int subtract(int a, int b) {
        return a - b;
    }

    public static void main(String[] args) {
        // Unit tests for the library
        System.out.println(&quot;Add test: &quot; + (add(2, 3) == 5 ? &quot;PASS&quot; : &quot;FAIL&quot;));
        System.out.println(&quot;Subtract test: &quot; + (subtract(5, 2) == 3 ? &quot;PASS&quot; : &quot;FAIL&quot;));
    }
}

