def create_vlan_segment(vlan_id, subnet, ports):
    payload = {
        &quot;name&quot;: f&quot;VLAN_{vlan_id}&quot;,
        &quot;vlan_id&quot;: vlan_id,
        &quot;subnet&quot;: subnet,
        &quot;ports&quot;: ports
    }
    response = requests.post(f&quot;{SDN_CONTROLLER_URL}/segments&quot;, json=payload, headers=HEADERS)
    response.raise_for_status()
    print(f&quot;Created VLAN segment {vlan_id}&quot;)

def move_vm_to_segment(vm_id, new_segment_id):
    payload = {
        &quot;vm_id&quot;: vm_id,
        &quot;segment_id&quot;: new_segment_id
    }
    response = requests.put(f&quot;{SDN_CONTROLLER_URL}/vm_assignments&quot;, json=payload, headers=HEADERS)
    response.raise_for_status()
    print(f&quot;Moved VM {vm_id} to segment {new_segment_id}&quot;)

