SELECT
    vulnerability_id,
    cvss_score,
    discovery_date,
    CASE
        WHEN cvss_score &gt;= 9.0 THEN &#39;Critical&#39;
        WHEN cvss_score &gt;= 7.0 THEN &#39;High&#39;
        WHEN cvss_score &gt;= 4.0 THEN &#39;Medium&#39;
        ELSE &#39;Low&#39;
    END AS severity,
    CASE
        WHEN cvss_score &gt;= 9.0 AND DATEDIFF(CURDATE(), discovery_date) &gt; 7 THEN &#39;Overdue&#39;
        ELSE &#39;On Track&#39;
    END AS remediation_status
FROM
    vulnerabilities
ORDER BY
    cvss_score DESC, discovery_date ASC;

