import mimetypes

def classify_file(filepath):
    mime_type, _ = mimetypes.guess_type(filepath)
    if mime_type:
        print(f&quot;File: {filepath}, Detected MIME Type: {mime_type}&quot;)
        # Logic to assign sensitivity based on MIME type or content
        if &#39;application/pdf&#39; in mime_type or &#39;text/plain&#39; in mime_type:
            print(&quot;Potential document, further review for sensitivity.&quot;)
        elif &#39;image/&#39; in mime_type:
            print(&quot;Image file, typically lower sensitivity unless PII is present.&quot;)
    else:
        print(f&quot;File: {filepath}, MIME Type: Unknown&quot;)

