# Example of a simplified &#39;gated&#39; configuration snippet for route filtering

# Deny routes to specific internal networks from being advertised externally
export protocol bgp {
    group external {
        peer 192.0.2.1 {
            restrict {
                deny { network 10.0.0.0/8; };
            };
        };
    };
};

# Accept RIP routes but apply a filter
export protocol rip {
    interface eth0 {
        restrict {
            permit { network 172.16.0.0/16; };
            deny all;
        };
    };
};

