# Example PowerShell to check for document classification metadata (conceptual)
Get-ChildItem -Path &quot;C:\SensitiveDocs&quot; -Recurse | ForEach-Object {
    $filePath = $_.FullName
    try {
        $shell = New-Object -ComObject Shell.Application
        $folder = $shell.NameSpace((Split-Path $filePath))
        $file = $folder.ParseName((Split-Path $filePath -Leaf))
        $classification = $folder.GetDetailsOf($file, 21) # Example for &#39;Security&#39; property index
        if ($classification -ne $null -and $classification -ne &quot;&quot;) {
            Write-Host &quot;File: $($filePath) - Classification: $($classification)&quot;
        } else {
            Write-Host &quot;File: $($filePath) - No Classification Metadata Found&quot;
        }
    } catch {
        Write-Warning &quot;Could not process file $($filePath): $($_.Exception.Message)&quot;
    }
}

