import numpy as np

def monte_carlo_attack_sim(num_simulations, success_rate_per_attempt):
    successful_attacks = 0
    for _ in range(num_simulations):
        if np.random.rand() &lt; success_rate_per_attempt:
            successful_attacks += 1
    return successful_attacks / num_simulations

# Example usage for trivial attacks
likelihood = monte_carlo_attack_sim(10000, 0.05) # 5% chance per attempt
print(f&quot;Simulated likelihood of successful trivial attack: {likelihood:.2%}&quot;)

