/* geoip.c
Return the country code and name for a given network
address

to compile: gcc -Wall -O2 -o geoip geoip.c -
L/usr/local/lib -lGeoIP
*/
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;GeoIP.h&gt;

int main(int argc, char** argv)
{
GeoIP * gi;
const char * returnedCountry;
char hostip[256];
char *p;

gi = GeoIP_open(&quot;/usr/local/share/GeoIP/GeoIP.dat&quot;,
GEOIP_STANDARD);

while ( fgets(hostip,255,stdin) )
{
if ( (p = rindex(hostip,&#39;\n&#39;)) ) *p = &#39;\0&#39;;
if ( (returnedCountry =
GeoIP_country_name_by_addr(gi,hostip)) == (char *)NULL )
returnedCountry = &quot;(unknown)&quot;;
printf(&quot;%s %s\n&quot;, hostip, returnedCountry);
}

GeoIP_delete(gi);
return 0;
}

# Example of a log archiving script for Linux systems
#!/bin/bash

LOG_DIR=&quot;/var/log&quot;
ARCHIVE_DIR=&quot;/mnt/log_archive/$(hostname)&quot;
RETENTION_DAYS=365

mkdir -p &quot;$ARCHIVE_DIR&quot;

# Archive logs older than 7 days
find &quot;$LOG_DIR&quot; -type f -name &quot;*.log&quot; -mtime +7 -exec mv {} &quot;$ARCHIVE_DIR&quot; \;

# Delete archived logs older than retention policy
find &quot;$ARCHIVE_DIR&quot; -type f -mtime +&quot;$RETENTION_DAYS&quot; -delete

