import boto3

def audit_mfa_for_privileged_users(aws_profile=&#39;default&#39;):
    session = boto3.Session(profile_name=aws_profile)
    iam = session.client(&#39;iam&#39;)

    privileged_roles = [&#39;AdministratorAccess&#39;, &#39;PowerUserAccess&#39;] # Example roles

    print(&#39;Auditing MFA status for privileged users:&#39;)
    users_with_mfa = set()

    # Check users directly assigned privileged policies
    for user in iam.list_users()[&#39;Users&#39;]:
        attached_policies = iam.list_attached_user_policies(UserName=user[&#39;UserName&#39;])[&#39;AttachedPolicies&#39;]
        for policy in attached_policies:
            if policy[&#39;PolicyName&#39;] in privileged_roles:
                mfa_devices = iam.list_mfa_devices(UserName=user[&#39;UserName&#39;])[&#39;MFADevices&#39;]
                status = &#39;Enabled&#39; if mfa_devices else &#39;Disabled&#39;
                print(f&quot;  User: {user[&#39;UserName&#39;]}, Privileged Role: {policy[&#39;PolicyName&#39;]}, MFA Status: {status}&quot;)
                if mfa_devices: users_with_mfa.add(user[&#39;UserName&#39;])
                break

    # Check users in privileged groups
    for group in iam.list_groups()[&#39;Groups&#39;]:
        group_policies = iam.list_attached_group_policies(GroupName=group[&#39;GroupName&#39;])[&#39;AttachedPolicies&#39;]
        for policy in group_policies:
            if policy[&#39;PolicyName&#39;] in privileged_roles:
                for user_in_group in iam.get_group(GroupName=group[&#39;GroupName&#39;])[&#39;Users&#39;]:
                    if user_in_group[&#39;UserName&#39;] not in users_with_mfa: # Avoid duplicate checks
                        mfa_devices = iam.list_mfa_devices(UserName=user_in_group[&#39;UserName&#39;])[&#39;MFADevices&#39;]
                        status = &#39;Enabled&#39; if mfa_devices else &#39;Disabled&#39;
                        print(f&quot;  User: {user_in_group[&#39;UserName&#39;]}, Privileged Group: {group[&#39;GroupName&#39;]}, MFA Status: {status}&quot;)
                        if mfa_devices: users_with_mfa.add(user_in_group[&#39;UserName&#39;])

# Example usage:
audit_mfa_for_privileged_users()

