public class ExampleEncapsulation {
    private int privateData; // Encapsulated data

    public ExampleEncapsulation(int data) {
        this.privateData = data;
    }

    public int getPrivateData() { // Public method to access data
        return privateData;
    }

    public void setPrivateData(int newData) { // Public method to modify data
        if (newData &gt; 0) { // Can include validation logic
            this.privateData = newData;
        }
    }
}

