public void enqueue(Item item)
{
    Node oldlast = last;
    last = new Node();
    last.item = item;
    last.next = null;
    if (isEmpty()) first = last;
    else oldlast.next = last;
    N++;
}

public Item dequeue()
{
    Item item = first.item;
    first = first.next;
    if (isEmpty()) last = null;
    N--;
    return item;
}

