        this.V = V;
        this.E = 0;
        adj = (Bag&lt;Integer&gt;[]) new Bag[V];
        for (int v = 0; v &lt; V; v++)
            adj[v] = new Bag&lt;Integer&gt;();
    }

    public void addEdge(int v, int w) {
        adj[v].add(w);
        adj[w].add(v);
        E++;
    }

    public Iterable&lt;Integer&gt; adj(int v) {
        return adj[v];
    }
