NetworkCommunicationEvents
| where RemoteIP == &#39;&lt;alert_dest_ip&gt;&#39;
| where LocalIP == &#39;&lt;alert_source_ip&gt;&#39;
| project Timestamp, InitiatingProcessFileName, InitiatingProcessCommandLine, InitiatingProcessParentFileName, InitiatingProcessParentCommandLine, RemoteIP, RemotePort, LocalIP, LocalPort, DeviceName, AccountName

index=container_events container_name=&quot;web-app-prod-01&quot; &quot;/host/var/run/docker.sock&quot; 
| table _time, user, image, command, mounts, host_ip 
| join type=left user [search index=auth_logs user=&quot;devops-svc-acct&quot; | table _time, user, auth_status] 
| where isnotnull(auth_status) AND auth_status=&quot;success&quot;

DeviceProcessEvents
| where InitiatingProcessCommandLine contains &quot;malicious-domain.xyz&quot; or RemoteUrl contains &quot;malicious-domain.xyz&quot;
| where DeviceName == &quot;&lt;InternalClientHostname&gt;&quot;
| project Timestamp, FileName, ProcessCommandLine, InitiatingProcessFileName, RemoteUrl, RemoteIP

index=dns sourcetype=bind_query client_ip=&quot;&lt;InternalClientIP&gt;&quot; query=&quot;malicious-domain.xyz&quot; 
| join type=left client_ip [search index=endpoint sourcetype=sysmon | stats count by ComputerName, Image, CommandLine] 
| table _time, client_ip, query, ComputerName, Image, CommandLine

DeviceNetworkEvents
| where RemoteIP == &#39;&lt;c2_server_ip&gt;&#39;
| where DeviceName == &#39;&lt;critical_server_hostname&gt;&#39;
| project Timestamp, InitiatingProcessFileName, InitiatingProcessCommandLine, RemoteIP, RemotePort

DeviceProcessEvents
| where FileName == &quot;powershell.exe&quot;
| where InitiatingProcessFileName == &quot;winword.exe&quot;
| project Timestamp, DeviceName, InitiatingProcessCommandLine, ProcessCommandLine, AccountName

DeviceProcessEvents
| where FileName == &quot;lsass.exe&quot;
| where ActionType == &quot;ProcessHandleOpen&quot;
| where InitiatingProcessFileName !in (&quot;winlogon.exe&quot;, &quot;services.exe&quot;, &quot;csrss.exe&quot;) // Exclude common legitimate processes
| where ProcessAccessRights has &quot;PROCESS_VM_READ&quot;
| project Timestamp, DeviceName, InitiatingProcessFileName, InitiatingProcessCommandLine, ProcessAccessRights

DeviceProcessEvents
| where FileName =~ &quot;HandleDuplication.exe&quot;
| project Timestamp, DeviceName, InitiatingProcessFileName, InitiatingProcessCommandLine, ProcessCommandLine, RemoteIP, RemotePort, InitiatingProcessParentFileName, InitiatingProcessParentCommandLine
| join kind=leftouter (
    DeviceNetworkEvents
    | where InitiatingProcessFileName =~ &quot;HandleDuplication.exe&quot;
    | project Timestamp, DeviceName, RemoteIP, RemotePort, ActionType
) on DeviceName, Timestamp

index=windows EventCode=4625 OR EventCode=4624 dest_ip=&quot;&lt;critical_server_IP&gt;&quot; src_ip=&quot;192.0.2.10&quot; 
| stats count(eval(EventCode=4625)) as FailedLogins, count(eval(EventCode=4624)) as SuccessfulLogins, values(TargetUserName) as Usernames by src_ip, dest_ip 
| where FailedLogins &gt; 0 AND SuccessfulLogins &gt; 0 AND mvcount(Usernames) &gt; 1

index=windows EventCode=4625 user=&quot;svc_account_prod&quot; 
| stats count by _time, src_ip, ComputerName 
| sort -_time

IdentityDirectoryEvents
| where ActionType == &quot;ServiceCreated&quot;
| where TargetDeviceName contains &quot;DC&quot;
| join kind=inner (DeviceProcessEvents
    | where InitiatingProcessCommandLine contains &quot;powershell&quot; or InitiatingProcessCommandLine contains &quot;cmd.exe&quot;
    | project DeviceName, Timestamp, InitiatingProcessCommandLine, FileName)
    on $left.TargetDeviceName == $right.DeviceName and $left.Timestamp between ($right.Timestamp - 1h .. $right.Timestamp + 1h)
| project Timestamp, TargetDeviceName, ActionType, InitiatingProcessCommandLine, FileName

index=ids sourcetype=snort OR sourcetype=suricata signature=&quot;SYN Flood&quot; OR signature=&quot;DoS&quot; 
| stats count by src_ip, dest_ip, signature 
| sort -count

index=ids sourcetype=5g_network_alerts &quot;Anomalous signal detected&quot; 
| join type=left _time 
    [search index=network_traffic sourcetype=5g_traffic_logs &quot;unusual data traffic patterns&quot; 
    | fields _time, src_ip, dest_ip, bytes_in, bytes_out] 
| table _time, alert_id, signal_location, estimated_tx_power, num_transmitters, src_ip, dest_ip, bytes_in, bytes_out

