AWSCloudTrail
| where TimeGenerated &gt; ago(1h)
| where SourceIpAddress == &#39;web-app-01_private_ip&#39; or UserIdentityArn contains &#39;web-app-01_role_arn&#39;
| where EventName contains &#39;S3&#39;
| project TimeGenerated, EventName, UserIdentityType, UserIdentityArn, SourceIpAddress, RequestParameters

