| join _time host 
    [search index=pcap host=&lt;affected_host_from_alert&gt; 
    | fields _time, src_ip, dest_ip, dest_port, bytes, protocol, raw_data] 
| table _time, signature, src_ip, dest_ip, dest_port, raw_data

