private void sort(Comparable[] a, int lo, int hi)
{
    if (hi &lt;= lo) return;
    int lt = lo, i = lo+1, gt = hi;
    Comparable v = a[lo];
    while (i &lt;= gt)
    {
        int cmp = a[i].compareTo(v);
        if      (cmp &lt; 0) exch(a, lt++, i++);
        else if (cmp &gt; 0) exch(a, i, gt--);
        else              i++;
    }
    sort(a, lo, lt-1);
    sort(a, gt+1, hi);
}
