# Example Zero Trust Access Policy (simplified)
policy_name: &#39;Internal_App_Access&#39;
subjects:
  - type: &#39;user&#39;
    attributes:
      department: &#39;finance&#39;
      role: &#39;analyst&#39;
  - type: &#39;device&#39;
    attributes:
      os_version: &#39;&gt;=Windows 10 22H2&#39;
      patch_level: &#39;current&#39;
      antivirus_status: &#39;running_and_up_to_date&#39;
resources:
  - type: &#39;application&#39;
    name: &#39;FinancialReportingApp&#39;
    action: &#39;read_write&#39;
conditions:
  - type: &#39;time_of_day&#39;
    range: &#39;08:00-17:00&#39;
  - type: &#39;geo_location&#39;
    country: &#39;US&#39;
allow_if_all_match: true

filter{
  if [syslog_priority] == &quot;info&quot; {
    drop{}
  }
  if [datasource] == &quot;Windows-Defender&quot; and [Event_Id] in [&quot;800&quot;, &quot;700&quot;] {
    drop{}
  }
}
