index=network (sourcetype=netflow OR sourcetype=firewall OR sourcetype=router) 
| timechart span=1m avg(latency) as avg_latency, sum(retransmissions) as total_retransmissions, sum(bytes_out) as total_throughput 
| where avg_latency &gt; &lt;threshold&gt; AND total_retransmissions &gt; &lt;threshold&gt; AND total_throughput &lt; &lt;expected_value&gt;

