import numpy as np

def calculate_mse(image_a, image_b):
    return np.mean((image_a - image_b)**2)

def calculate_psnr(image_a, image_b, max_pixel_value=255):
    mse = calculate_mse(image_a, image_b)
    if mse == 0: return float(&#39;inf&#39;)
    return 20 * np.log10(max_pixel_value / np.sqrt(mse))

